mapper.insert:
INSERT INTO `board`.`board_board`
(
 board_title,
 board_content,
 reg_no
 )
VALUES
(
 #{board_title},
 #{board_content},
 #{reg_no}
);

mapper.select:
SELECT board.board_no as board_no ,board.board_title as board_title ,board_content as board_content ,board.reg_date as reg_date ,board.update_date as update_date ,board.reg_no as reg_no ,member.user_id as reg_id FROM board_board board LEFT JOIN board_member member ON board.reg_no = member.user_no WHERE 1=1
AND board.board_title = #{board_title}
AND board.board_no = #{board_no}
And board.reg_no = #{reg_no}
LIMIT #{current_page_no}, #{page_size}

SELECT 
		count(1)
FROM board_board board
LEFT JOIN board_member member
ON board.reg_no = member.user_no
WHERE 1=1

	AND board.board_title = #{board_title}


	AND board.board_no = #{board_no}


	And board.reg_no = #{reg_no}

mapper.update:
UPDATE board_board
SET
	board_title = #{board_title}
	,board_content = #{board_content}
	,reg_date = now()
	,update_date = now()
WHERE board_no = #{board_no};
